<?phprequire_once realpath(dirname(dirname(__DIR__)) . DIRECTORY_SEPARATOR . 'indexer.php');use newznab\db\Settings;use newznab\controllers\Releases;use newznab\controllers\ReleaseExtra;$r = new Releases();$rv = new ReleaseExtra();$db = new Settings();$rels = $db->query("select releaseID, videowidth, videoheight from releasevideo where definition is null");foreach($rels as $rel){	$sql = sprintf("update releasevideo set definition = %d where releaseID = %d",			$rv->determineVideoResolution($rel["videowidth"], $rel["videoheight"]), $rel["releaseID"]);	$db->exec($sql);}?>